let offsetA = -0.0909;

msg.payload.p.b = Math.abs((msg.payload.p.a + offsetA ) - (msg.payload.p.b + offsetB ));
msg.payload.p.b = msg.payload.p.b / 0.15

msg.payload.vcc = msg.payload.p.a.toFixed(2);
msg.payload.vsys = msg.payload.p.s.toFixed(2);
