const data = {
    "series" : ["position"],
    "data": [msg.payload.range],
    "labels": ["range"],
}

// write data to msg.payload
msg.payload = [data]

// return data object
return msg;

msg.payload.angle

function map_range(value, low1, high1, low2, high2) {
    return low2 + (high2 - low2) * (value - low1) / (high1 - low1);
}


for (let index = 0; index < msg.payload.angle.length; index++) {
    msg.payload.angle[index] = msg.payload.angle[index] * (180/Math.PI);
    msg.payload.angle[index] =  map_range(msg.payload.angle[index], -180.0, 180.0, 0.0, 360.0);
}


return msg;
